// Fetching all regions weather data
const fetchData = async () => {
    regions.value.map(async (region) => {
        const { data } = await axios.get("/onecall", {
            params: {
                lat: region.lat,
                lon: region.lon,
                exclude: "minutely,hourly,alerts",
                appid: "9dd86907fe501cec50da3d087e4e9dc0",
                units: "metric",
                lang: "en"
            }
        });

        const newData = {
            id: region.id,
            name: region.name,
            temp: data.current.temp,
            description: data.current.weather[0].description,
            icon: data.current.weather[0].icon,
            humidity: data.current.humidity,
            wind: data.current.wind_speed,
            clouds: data.current.clouds,
        }

        regionsWeather.value = [...regionsWeather.value, newData];
    })
}

// Fetching current region weather data
const fetchCurrentData = async (region) => {
    const { data } = await axios.get("/onecall", {
        params: {
            lat: region.lat,
            lon: region.lon,
            exclude: "minutely,hourly,alerts",
            appid: "9dd86907fe501cec50da3d087e4e9dc0",
            units: "metric",
            lang: "en"
        }
    })

    const newData = {
        id: region.id,
        name: region.name,
        temp: data.current.temp,
        description: data.current.weather[0].description,
        icon: data.current.weather[0].icon,
        humidity: data.current.humidity,
        current: data.daily[0],
        daily: data.daily.slice(1, -1)
    }

    currentRegion.value = newData;
}

onMounted(() => {
    fetchData().then(() => setTimeout(() => isLoaderVisible.value = false, 1000))
});

</script>